// The localValue can only be used in file a.js.
var
 localValue = 
'a'
// Get the app instance.
var
 app = getApp()

// Get the global data and change it.

app.globalData++
// b.js
// You can redefine localValue in file b.js, without interference with the localValue in a.js.
var
 localValue = 
'b'
